var payload = msg.payload || 0;
msg.payload = false;

switch (typeof payload) {
    case 'number':
        switch (payload) {
            case 1:
            case 2:
                msg.payload = true;
                break;
        }
        break;
    case 'string':
        switch (payload.toLowerCase()) {
            case "on":
            case "true":
                msg.payload = true;
                break;
        }
        break;
    default: msg.payload = payload;
}

return {payload: msg.payload};
