func:
var content = msg.message;

msg.payload = {};
msg.payload.chatId = msg.chatId;
msg.payload.options = {
    parse_mode : "Markdown"
};
msg.payload.content = content.toString();
msg.payload.type = 'message';

return [msg];

msg.payload = {};
msg.payload.chatId = msg.chatId;
msg.payload.content = msg.sentMessageId;
msg.payload.type = 'deleteMessage';
msg.payload.options = {
    message_id : msg.sentMessageId,
    chat_id : msg.payload.chatId
};

return [msg];

let map = global.get("disposalData") || {};
delete map["today"];
delete map["tomorrow"];

let upcoming = false;
let disposalDates = msg.payload;
for (var i = 0; i < disposalDates.length; i++) {
    let key = disposalDates[i].topic;
    let countdown = disposalDates[i].countdown.days + 1;

    map[key] = countdown || 0;

    if (countdown == 0) {
        if (map["today"] === undefined) {
            map["today"] = key;
        } else {
            map["today"] += " und " + key;
        }
        upcoming = true;

    } else if (countdown == 1) {
        if (map["tomorrow"] === undefined) {
            map["tomorrow"] = key;
        } else {
            map["tomorrow"] += " und " + key;
        }
        upcoming = true;
    }
}

global.set("disposalData", map);

let map = global.get('disposalData') || {};

if (map["tomorrow"] !== undefined) {
    msg.message = "♲ Morgen wird " + map["tomorrow"] + " abgeholt.";
    return msg;
}

let map = global.get('disposalData') || {};

if (map["today"] !== undefined) {
    msg.message = "♲ Heute wird " + map["today"] + " abgeholt.";
    return msg;
}
